import axios from 'axios';

const API_BASE = 'http://localhost:3009';

const get = (path) => axios.get(`${API_BASE}/${path}`);
const post = (path, body) => axios.post(`${API_BASE}/${path}`, body);
const del = (path, body) => axios.delete(`${API_BASE}/${path}`, body);

export {
  get,
  post,
  del,
}
