const axios = require('axios');

const Index = ({todos}) => {
  return (
    <div>
      {todos.map(todo => (
        <div>{todo.title}</div>
      ))}
    </div>
  )
}

Index.getInitialProps = async function () {
  const response = await axios.get('http://localhost:3009/todos');
  console.log(response.data)
  return {
    todos: response.data,
    // shows: data.map(entry => entry.show)
  };
};

export default Index;
